<style>
  .switches-container {
    width: fit-content;
    padding: .4rem;
    border-radius: 100%;
    rotate: 45deg;
    border: 1px solid transparent;
    background-color: var(--on-background-color);
  }
  .cardinal-levers-container {
    background-color: #d9d9d9;
    border-radius: 100%;
    height: 71px;
    width: 71px;
    display: flex;
    justify-content: center;
    align-items: center;
    border: 4px solid #d9d9d9;
  }
  .cardinal-levers-container.active {
    border: 4px solid var(--active-highlight-color);
  }

  .switches-container.active {
    background-color: var(--active-highlight-color);
    border: 1px solid var(--active-highlight-color);
  }

  .kbd {
      display: inline-flex;
      align-items: center;
      justify-content: center;
      border-width: 1px;
      border-color: hsl(215 28% 17%);
      background-color: #D9D9D9;
      border-radius: .3rem;
      border-bottom-width: 2px;
      min-height: 1.8em;
      min-width: 1.8em;
      margin: .1em;
      rotate: -45deg;
  }
  .flex {
    display: flex;
  }
  .ml-1 {
    margin-left: .25rem;
  }
  .items-center{
    align-items: center;
  }
</style>
